mapper.select:
select q.*, u.real_name as change_user ,u1.real_name as createName from tb_question q left join tb_user u on u.id = q.oper_user left join tb_user u1 on u1.id = q.create_user
AND q.type = #{param.type}
AND q.group_id = #{param.groupId}
AND q.hard = #{param.hard}
AND q.create_user = #{param.createUser}
AND (q.options like "%"#{param.keyword}"%" OR q.answer like "%"#{param.keyword}"%" OR q.topic like "%"#{param.keyword}"%" )
and q.id NOT IN ( SELECT tqp.question_id FROM tb_question_paper tqp WHERE tqp.paper_id = #{param.selectPaperId} )

select qg.*,count(q.id) questionNumber from tb_question_group qg left join tb_question q on q.group_id = qg.id

    AND q.create_user = #{userId}

group by qg.id
